--
-- Cấu trúc bảng cho bảng `wp_posts`
--

CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(255) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT 0,
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- Đang đổ dữ liệu cho bảng `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2024-09-20 06:16:47', '2024-09-20 06:16:47', '<!-- wp:paragraph -->\r\n<h1 class=\"title-detail\">Quang Dũng đầy tâm trạng khi hát về nỗi cô đơn</h1>\r\n<p class=\"description\">Với giọng hát chứa nhiều nỗi u hoài, nam ca sĩ thể hiện nhạc phẩm \"Đời có bao nhiêu ngày vui\" như tâm sự với người nghe về tình yêu, cuộc đời và cả cái chết.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Từ lâu, ca sĩ Quang Dũng trung thành với dòng nhạc trữ tình và phong cách lịch lãm qua các sản phẩm âm nhạc cũng như khi trình diễn trên sân khấu. Luôn tâm niệm được thể hiện đúng bản chất âm nhạc của mình nhưng phải làm sao khán giả không \"ngán\", anh thường chịu khó tìm tòi, đầu tư ca khúc độc quyền phù hợp với sở trường.</p>\r\n<p class=\"Normal\">Chuẩn bị giới thiệu sản phẩm âm nhạc mới, Quang Dũng đặt hàng tác giả trẻ Châu Đăng Khoa các ca khúc không chỉ nói về tình yêu đôi lứa, mà mở rộng chủ đề về cuộc đời, con người, những suy tư mang tính triết lý...</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"js\">\r\n<div class=\"fig-picture\" style=\"margin: 0px; padding: 0px 0px 340px; box-sizing: border-box; text-rendering: optimizelegibility; width: 680px; float: left; display: table; -webkit-box-pack: center; justify-content: center; background: #f0eeea; text-align: center; position: relative;\" data-src=\"https://i1-giaitri.vnecdn.net/2014/08/20/T3H0112-1515-1408522733.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=HX3vwd8SSR12-n9PJ8coWA\" data-sub-html=\"&lt;div class=&quot;ss-wrapper&quot;&gt;&lt;div class=&quot;ss-content&quot;&gt;\r\n&lt;p class=&quot;Image&quot;&gt;\r\nCa sĩ Quang Dũng và diễn viên Phương Khánh trong MV.&lt;/p&gt;\r\n&lt;/div&gt;&lt;/div&gt;\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2014/08/20/T3H0112-1515-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XaeEZrkhDCVeBST0dxnoJw\" data-srcset=\"https://i1-giaitri.vnecdn.net/2014/08/20/T3H0112-1515-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XaeEZrkhDCVeBST0dxnoJw\" /><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2014/08/20/T3H0112-1515-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XaeEZrkhDCVeBST0dxnoJw\" alt=\"Ca sĩ Quang Dũng và diễn viên Phương Khánh có nhiều cảnh sâu lắng trong MV.\" data-src=\"https://i1-giaitri.vnecdn.net/2014/08/20/T3H0112-1515-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=XaeEZrkhDCVeBST0dxnoJw\" data-ll-status=\"loaded\" /></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Ca sĩ Quang Dũng và diễn viên Phương Khánh trong MV.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Khi được Châu Đăng Khoa gửi cho bài hát <em>Đời có bao nhiêu ngày vui</em>, Quang Dũng rất tâm đắc với ca từ, giai điệu ballad nồng nàn của nó. Anh vào phòng thu ghi âm bài hát nhiều lần, nhưng vẫn chưa vừa ý. Anh còn mang sản phẩm thu thử ra bàn bạc cùng nhạc sĩ sinh năm 1990. Sau một thời gian, Quang Dũng mới có được bản thu âm khiến anh hài lòng. Sự biến chuyển trong tâm trạng của nhân vật trong bài hát được anh thể hiện sâu lắng, nhiều cao trào cảm xúc với những day dứt khi nói về cuộc đời ngắn ngủi, nỗi cô đơn và những giá trị của cuộc sống.</p>\r\n<p class=\"Normal\">Ca khúc này được Châu Đăng khoa sáng tác trong một lần anh rơi vào tâm trạng nặng nề, cô đơn cùng cực khi gặp sự cố trong công việc, cãi nhau với bạn bè và nhìn cuộc sống với màu sắc bi quan. Sau một đêm trằn trọc và mất ngủ, lời ca khúc và giai điệu bài hát xuất hiện trong đầu tác giả 9X. \"Tôi muốn trong bài hát của mình, người ta dám đối diện với cảm xúc thật trong tâm hồn để rồi sau đó thấy quý và trân trọng hơn khoảnh khắc đẹp trong cuộc sống\", tác giả ca khúc tâm sự.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"js\">\r\n<div class=\"fig-picture\" style=\"margin: 0px; padding: 0px 0px 279.094px; box-sizing: border-box; text-rendering: optimizelegibility; width: 680px; float: left; display: table; -webkit-box-pack: center; justify-content: center; background: #f0eeea; text-align: center; position: relative;\" data-src=\"https://i1-giaitri.vnecdn.net/2014/08/20/MV-2-1486-1408522733.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=lmVY6WuGmPRMEj9GTP-8mw\" data-sub-html=\"&lt;div class=&quot;ss-wrapper&quot;&gt;&lt;div class=&quot;ss-content&quot;&gt;\r\n&lt;p class=&quot;Image&quot;&gt;\r\nNghệ sĩ Ưu tú Phi Điểu (trái) và nghệ sĩ Mai Thành là đôi bạn già đẹp trong những thước phim đầy hoài niệm.&lt;/p&gt;\r\n&lt;/div&gt;&lt;/div&gt;\"><picture><source srcset=\"https://i1-giaitri.vnecdn.net/2014/08/20/MV-2-1486-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ii9Vd1CQAYS1Bde_xn8Drg\" data-srcset=\"https://i1-giaitri.vnecdn.net/2014/08/20/MV-2-1486-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ii9Vd1CQAYS1Bde_xn8Drg\" /><img class=\"lazy lazied\" src=\"https://i1-giaitri.vnecdn.net/2014/08/20/MV-2-1486-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ii9Vd1CQAYS1Bde_xn8Drg\" alt=\"Nghệ sĩ Ưu tú Phi Điểu (trái) và nghệ sĩ Mai Thành là đôi bạn già đẹp với những thước phim đầy chất hoài niệm.\" data-src=\"https://i1-giaitri.vnecdn.net/2014/08/20/MV-2-1486-1408522733.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ii9Vd1CQAYS1Bde_xn8Drg\" data-ll-status=\"loaded\" /></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nghệ sĩ Ưu tú Phi Điểu (trái) và nghệ sĩ Mai Thành là đôi bạn già đẹp trong những thước phim đầy hoài niệm.</p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Sau một thời gian dài không ra MV, lần này, Quang Dũng quyết định thực hiện video cho ca khúc <em>Đời có bao nhiêu ngày vui</em>. MV được thực hiện với êkíp đạo diễn Trần Gia Lạc cùng các diễn viên Mai Thành, Nghệ sĩ Ưu tú Phi Điểu và diễn viên trẻ Nguyễn Phương Khánh.</p>\r\n<p class=\"Normal\">Hai diễn viên cao tuổi Mai Thành và Phi Điểu tạo thành đôi bạn già với hình ảnh nhiều hoài niệm và trăn trở về đời người. Trong khi đó, Quang Dũng một lần nữa chứng tỏ khả năng diễn xuất khi cùng bạn diễn xinh đẹp kể lại câu chuyện về lòng chung thủy trong tình yêu.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"js\">\r\n<div class=\"action_thumb flexbox\"> </div>\r\n<div class=\"fig-picture\" style=\"margin: 0px; padding: 0px 0px 487.156px; box-sizing: border-box; text-rendering: optimizelegibility; width: 680px; float: left; display: table; -webkit-box-pack: center; justify-content: center; background: #f0eeea; text-align: center; position: relative;\" data-src=\"https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw\" data-sub-html=\"&lt;div class=&quot;ss-wrapper&quot;&gt;&lt;div class=&quot;ss-content&quot;&gt;&lt;p class=&quot;Image&quot;&gt;\r\n&lt;/p&gt;&lt;div style=&quot;text-align:center;&quot;&gt;\r\n&lt;!--start video embed--&gt;\r\n&lt;div onclick=&quot;try{Video.playVideo(23404);}catch(e){}&quot; id=&quot;video_parent_23404&quot; class=&quot;box_embed_video_parent embed_video_new&quot; data-vcate=&quot;1003834&quot; data-vscate=&quot;1004235&quot; data-vscaten=&quot;Xã hội&quot; data-vid=&quot;23404&quot; data-ratio=&quot;1&quot; data-articleoriginal=&quot;0&quot; data-ads=&quot;1&quot; data-license=&quot;&quot; data-duration=&quot;403&quot; data-brandsafe=&quot;0&quot; data-type=&quot;0&quot; data-play=&quot;0&quot; data-frame=&quot;&quot; data-aot=&quot;&quot; data-videoclassify=&quot;None&quot;&gt;\r\n&lt;div data-vid=&quot;23404&quot; class=&quot;box_img_video embed-container&quot;&gt;\r\n&lt;img src=&quot;https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;amp;h=0&amp;amp;q=100&amp;amp;dpr=1&amp;amp;fit=crop&amp;amp;s=J_XbqjFp35Y-80YOEdR4Nw&quot; alt=&quot;MV &amp;quot;Đời có bao nhiêu ngày vui&amp;quot;&quot;&gt;\r\n&lt;div class=&quot;icon_blockvideo&quot;&gt;\r\n&lt;div class=&quot;img_icon&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;div class=&quot;image_icon_center&quot;&gt;&amp;nbsp;&lt;/div&gt;\r\n&lt;/div&gt;&lt;/div&gt;\r\n&lt;div id=&quot;embed_video_23404&quot; class=&quot;box_embed_video&quot; style=&quot;display:none;&quot;&gt;\r\n&lt;div class=&quot;bg_overlay_small_unpin&quot;&gt;&lt;/div&gt;\r\n&lt;div class=&quot;bg_overlay_small&quot;&gt;&lt;/div&gt;\r\n&lt;div class=&quot;embed-container&quot;&gt;\r\n&lt;div id=&quot;video-23404&quot;&gt;\r\n&lt;div id=&quot;parser_player_23404&quot; class=&quot;media_content&quot; style=&quot;display:none;&quot;&gt;\r\n&lt;div id=&quot;videoContainter_23404&quot; class=&quot;videoContainter&quot; style=&quot;width: 100%; height: 100%;&quot;&gt;\r\n&lt;video id=&quot;media-video-23404&quot; preload=&quot;auto&quot; playsinline=&quot;&quot; webkit-playsinline=&quot;&quot; src=&quot;https://d1.vnecdn.net/giaitri/video/video/web/mp4/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541/index-v1-a1.m3u8&quot; type=&quot;application/x-mpegURL&quot; controls=&quot;&quot; style=&quot;width: 100%; height: 100%;&quot; data-mode=&quot;360&quot; max-mode=&quot;360&quot; data-subtitle=&quot;0&quot; active-mode=&quot;360&quot; ads=&quot;&quot; adsconfig=&quot;{&amp;quot;adlist&amp;quot;:[{&amp;quot;type&amp;quot;:&amp;quot;preroll&amp;quot;,&amp;quot;tag&amp;quot;:&amp;quot;https:\\/\\/pubads.g.doubleclick.net\\/gampad\\/ads?iu=\\/27973503\\/Vnexpress\\/Desktop\\/Instream.preroll\\/Giaitri\\/Giaitri.detail&amp;amp;description_url=http%3A%2F%2Fvnexpress.net&amp;amp;tfcd=0&amp;amp;npa=0&amp;amp;sz=640x360&amp;amp;gdfp_req=1&amp;amp;output=vast&amp;amp;unviewed_position_start=1&amp;amp;env=vp&amp;amp;impl=s&amp;amp;correlator=&amp;quot;,&amp;quot;skipOffset&amp;quot;:&amp;quot;00:00:06&amp;quot;,&amp;quot;duration&amp;quot;:&amp;quot;00:00:30&amp;quot;}]}&quot; data-ex=&quot;st=&amp;amp;bs=0&amp;amp;pt=0&quot;&gt;&lt;/video&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;parser_title&quot; style=&quot;display:none;&quot;&gt;MV &quot;Đời có bao nhiêu ngày vui&quot;&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;!--end video embed--&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;\"><picture><source srcset=\"https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw 1x, https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw 2x\" media=\"(min-width: 768px)\" data-srcset=\"https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw 1x, https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw 2x\" /><img class=\"lazy lazied\" src=\"https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw\" data-ll-status=\"loaded\" /></picture></div>\r\n<figcaption>\r\n<p class=\"Image\"> </p>\r\n<div>\r\n<div id=\"video_parent_23404\" class=\"box_embed_video_parent embed_video_new\" data-vcate=\"1003834\" data-vscate=\"1004235\" data-vscaten=\"Xã hội\" data-vid=\"23404\" data-ratio=\"1\" data-articleoriginal=\"0\" data-ads=\"1\" data-license=\"\" data-duration=\"403\" data-brandsafe=\"0\" data-type=\"0\" data-play=\"0\" data-frame=\"\" data-aot=\"\" data-videoclassify=\"None\" data-initdom=\"1\">\r\n<div class=\"box_img_video embed-container\" data-vid=\"23404\"><img src=\"https://iv1.vnecdn.net/giaitri/images/web/2014/08/20/1466062454/mv-doi-co-bao-nhieu-ngay-vui-1408509541.jpg?w=0&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=J_XbqjFp35Y-80YOEdR4Nw\" alt=\"MV &quot;Đời có bao nhiêu ngày vui&quot;\" /></div>\r\n</div>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n</article>\r\n<!-- /wp:paragraph -->\r\n\r\n<!-- wp:paragraph -->\r\n<p>&nbsp;</p>\r\n<!-- /wp:paragraph -->', 'Đời có bao nhiêu ngày dui', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2024-09-20 09:12:25', '2024-09-20 09:12:25', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2024-09-20 06:16:47', '2024-09-20 06:16:47', '<!-- wp:paragraph -->\n<p>This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...or something like this:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>As a new WordPress user, you should go to <a href=\"http://localhost/wordpress/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!</p>\n<!-- /wp:paragraph -->', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2024-09-20 06:16:47', '2024-09-20 06:16:47', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2024-09-20 06:16:47', '2024-09-20 06:16:47', '<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Who we are</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Our website address is: http://localhost/wordpress.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Comments</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>When visitors leave comments on the site we collect the data shown in the comments form, and also the visitor&#8217;s IP address and browser user agent string to help spam detection.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>An anonymized string created from your email address (also called a hash) may be provided to the Gravatar service to see if you are using it. The Gravatar service privacy policy is available here: https://automattic.com/privacy/. After approval of your comment, your profile picture is visible to the public in the context of your comment.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Media</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. Visitors to the website can download and extract any location data from images on the website.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Cookies</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you leave a comment on our site you may opt-in to saving your name, email address and website in cookies. These are for your convenience so that you do not have to fill in your details again when you leave another comment. These cookies will last for one year.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>If you visit our login page, we will set a temporary cookie to determine if your browser accepts cookies. This cookie contains no personal data and is discarded when you close your browser.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>When you log in, we will also set up several cookies to save your login information and your screen display choices. Login cookies last for two days, and screen options cookies last for a year. If you select &quot;Remember Me&quot;, your login will persist for two weeks. If you log out of your account, the login cookies will be removed.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>If you edit or publish an article, an additional cookie will be saved in your browser. This cookie includes no personal data and simply indicates the post ID of the article you just edited. It expires after 1 day.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Embedded content from other websites</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Articles on this site may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>These websites may collect data about you, use cookies, embed additional third-party tracking, and monitor your interaction with that embedded content, including tracking your interaction with the embedded content if you have an account and are logged in to that website.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Who we share your data with</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you request a password reset, your IP address will be included in the reset email.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">How long we retain your data</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you leave a comment, the comment and its metadata are retained indefinitely. This is so we can recognize and approve any follow-up comments automatically instead of holding them in a moderation queue.</p>\n<!-- /wp:paragraph -->\n<!-- wp:paragraph -->\n<p>For users that register on our website (if any), we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">What rights you have over your data</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>If you have an account on this site, or have left comments, you can request to receive an exported file of the personal data we hold about you, including any data you have provided to us. You can also request that we erase any personal data we hold about you. This does not include any data we are obliged to keep for administrative, legal, or security purposes.</p>\n<!-- /wp:paragraph -->\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Where your data is sent</h2>\n<!-- /wp:heading -->\n<!-- wp:paragraph -->\n<p><strong class=\"privacy-policy-tutorial\">Suggested text: </strong>Visitor comments may be checked through an automated spam detection service.</p>\n<!-- /wp:paragraph -->\n', 'Privacy Policy', '', 'draft', 'closed', 'open', '', 'privacy-policy', '', '', '2024-09-20 06:16:47', '2024-09-20 06:16:47', '', 0, 'http://localhost/wordpress/?page_id=3', 0, 'page', '', 0),
